const sshpk = require('sshpk');
const fs = require('fs');

const toFingerprint = sshKey =>
  sshpk
    .parseKey(sshKey, 'ssh')
    .fingerprint()
    .toString();

const main = async () => {
  const MariaSQL = require('mariasql');
  const Dao = require('../src/dao');

  const sqlClient = new MariaSQL({
    host: 'localhost',
    port: 3306,
    user: 'api',
    password: 'api',
    db: 'infrastructure',
  });

  const dao = Dao.make(sqlClient);
  const cred = { role: 'admin' };

  const ret = await dao.getCustomerSshKeys(cred);
  let priv;
  try {
    priv = fs.readFileSync('../lagoon_test', 'utf8');
  } catch (e) {
    console.error(
      'No `../lagoon_test` file found. It should contain the ssh private key for testing',
    );
    process.exit(1);
  }

  const foo = sshpk.parsePrivateKey(priv, 'ssh');
  console.log('FIRST FINGERPRINT:');
  console.log(foo.fingerprint().toString());
  console.log('SECOND FINTERPRINT:');
  console.log(
    foo
      .toPublic()
      .fingerprint()
      .toString(),
  );
  console.log('OTHER:');
  console.log(ret[0]);

  const key = ret[0];

  // const myKey =
  //   'ssh-rsa  p.stapfer@gmail.com';

  const fingerprint = toFingerprint(key);
  console.log(fingerprint);

  sqlClient.end();
};

main();
